    private void jLabel6MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabel6MouseClicked

        AlbumBO albo = new AlbumBO();
        ArtistaBO abo = new ArtistaBO();
        cbo = new CancionBO();

        String entidad = cBoxBuscarPor.getSelectedItem().toString().toLowerCase();
        String entidadNombre = txtNombre.getText();
        modelo2.clear();
        switch (entidad) {
            case "nombre de album":

                try {
                    album = albo.buscarAlbum(entidadNombre);
                    if (album.getNombre().equals(entidadNombre)) {
                        modelo.addElement(album);
                        listMostrar.setModel(modelo);
                        int size = cbo.buscarCancionesAlbum(album.getId_album()).size();
                        for (int i = 0; i < size; i++) {
                            Cancion cancion = (Cancion) cbo.buscarCancionesAlbum(album.getId_album()).get(i);
                            modelo2.addElement(cancion);
                        }
                        listDetalles.setModel(modelo2);
                    }
                } catch (Exception e) {
                    JOptionPane.showMessageDialog(this, "No se Encontró un Album relacionado!!");
                }
                break;

            case "nombre de artista":
                try {
                    artista = abo.buscarArtista(entidadNombre);
                    if (artista.getNombre().equals(entidadNombre)) {
                        modelo.addElement(artista);
                        modelo.addElement(artista.getAlbum());
                        listDetalles.setModel(modelo);
                    }
                } catch (Exception e) {
                    JOptionPane.showMessageDialog(this, "No se Encontró un Album relacionado!!");
                }
                break;

            case "nombre de cancion":
                try {
                    cancion = cbo.buscarCancion(entidadNombre);
                    if (cancion.getNombre().equals(entidadNombre)) {
                        modelo.addElement(cancion);
                        listMostrar.setModel(modelo);
                        modelo2.addElement(cancion);
                    }
                } catch (Exception e) {
                    JOptionPane.showMessageDialog(this, "No se Encontró un Cancion relacionada!!");
                }
                break;
            default:
                break;
        }
    }//GEN-LAST:event_jLabel6MouseClicked

