    private void listMostrarValueChanged(javax.swing.event.ListSelectionEvent evt) {//GEN-FIRST:event_listMostrarValueChanged
        modelo2.clear();
        if (listMostrar.getSelectedValue() == album) {
            int size = cbo.buscarCancionesAlbum(album.getId_album()).size();
            for (int i = 0; i < size; i++) {
                Cancion cancion = (Cancion) cbo.buscarCancionesAlbum(album.getId_album()).get(i);
                modelo2.addElement(cancion);
            }

        } else if (listMostrar.getSelectedValue() == cancion) {
            modelo2.addElement(cancion.get_album());
        }

        listDetalles.setModel(modelo2);

    }//GEN-LAST:event_listMostrarValueChanged

    private void lblCarritoMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_lblCarritoMouseClicked
        frmcarrito = new FrmCarrito(carrito);
        frmcarrito.setJf(this);
        frmcarrito.setVisible(true);
        this.setVisible(false);
    }//GEN-LAST:event_lblCarritoMouseClicked

